import React, {useEffect, useState} from "react";
import s from "./Booking.module.css";
import {NavLink, useHistory} from "react-router-dom";
import Header from "../Header_Footer/Header";
import pic1 from "./master.png";
import pic2 from "./services.png";
import { CalendarComponent } from "@syncfusion/ej2-react-calendars";
import axios from "axios";


export default function Booking() {

    const [barberName, setBarberName] = useState("");
    const [servicePrice, setServicePrice] = useState("");
    const [serviceName, setServiceName] = useState("");
    const [date, setDateName] = useState("");
    const history = useHistory();
    const [barber, setBarber] = useState([]);
    const [Email, setEmail] = useState("");

    let serviceName1 = localStorage.getItem('bookingServiceName-info');
    let servicePrice1 = localStorage.getItem('bookingServicePrice-info');
    let barber1 = localStorage.getItem('bookingBarber-info');
    let date1 = localStorage.getItem('bookingDate-info');
    let obj1 = JSON.parse(localStorage.getItem('user-info'));
    let email1 = obj1['email'];



    useEffect(() => {
        axios
            .get("https://craftcutstestapiproject20211120225609.azurewebsites.net/api/Barber")
            .then(
                (respon) => {
                    console.log(respon)
                    setBarber(respon.data)})
    },[]);

    const [service, setService] = useState([])
    useEffect(() => {
        axios
            .get("https://craftcutstestapiproject20211120225609.azurewebsites.net/api/Service")
            .then(
                (respon) => {
                    console.log(respon)
                    setService(respon.data)})
    },[]);




    localStorage.setItem("bookingServiceName-info", serviceName);
    localStorage.setItem("bookingServicePrice-info", servicePrice);
    localStorage.setItem("bookingBarber-info", barberName);
    localStorage.setItem("bookingDate-info", date);



    async function BookingApi(e) {
        e.preventDefault();
        let result = await fetch("https://craftcutstestapiproject20211120225609.azurewebsites.net/api/Booking/Web", {
            method: 'POST',
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
            },
            body: JSON.stringify({barberName, email1, serviceName, date}),
        });
        console.log(result);
        console.log(result.status);

        // console.log({barberName, customerEmail, hookServiceName, hookDate});
        console.log({barber1, email1, serviceName, date});
        result = await result.json();
        localStorage.setItem("booking-info", JSON.stringify(result));
        // localStorage.setItem('isLoggedIn', true);
        // setIsLoggedIn(true);
        history.push("/endbooking")
        console.log(result);
        console.log(result.status);
    }



    return (
        <div className={s.booking}>
            <div className={s.container}>
                <h1 className={s.h1Online}>Онлайн запись</h1>
                <h3 className={s.h3Choose}>Выберите необходимые варианты:</h3>
                <div className={s.container_choose}>

                    <form method="post">
                        <h4 className={s.h4choose}>Выберите мастера</h4>
                        <select onChange = {(e) => {const selectedmaster = e.target.value;
                            setBarberName(selectedmaster);}} key ={barber.id}>
                            {barber.map(barber =>
                                <option key = {barber.name}>{barber.name}</option>
                            )}
                        </select>
                        <h4 className={s.h4choose}>Выберите услугу</h4>
                        <select onChange = {(e) => {const selectedservice = e.target.value; setServiceName(selectedservice);}} key ={service.id}>
                            {service.map(service =>
                                <option value = {service.name}>{service.name}</option>
                            )}
                        </select>
                        <h4 className={s.h4choose}>Выберите время</h4>
                        <div className="mb-4">
                            <input onChange = {(e) => {const selecteddate= e.target.value;
                                setDateName(selecteddate);}}type="datetime-local" placeholder="password" />
                        </div>
                        <div className = {s.promo}>
                            <h4>У вас есть промокод?</h4>
                            <h6>Введите его здесь</h6>
                            <form>
                                <input type = "text" placeholder = "промокод"  className = {s.input_promo1}></input>
                                <button className={s.btn_accept}>Применить</button>
                            </form>
                        </div>
                        {/*<button onClick={BookingApi}>Записаться</button>*/}
                        <NavLink to={{ pathname: '/EndBooking',   state: {serviceName, servicePrice, barberName, date} }} onClick={BookingApi} type = "button" className = {s.btnbook}>Записаться</NavLink>
                    </form>
                    <div className={s.service}></div>
                    <div className={s.time}></div>
                </div>
            </div>
        </div>
    );
}
