import React, {useEffect, useState} from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import s from "./Login.module.css";
import imagecol from "./login_image.png";
import { NavLink } from "react-router-dom";
import Cabinet from "../UserCabinet/Cabinet";
import { useHistory } from 'react-router-dom';


export default function Login({setIsLoggedIn, isLoggedIn}) {
    const [Email, setEmail] = useState("");
    const [Password, setPassword] = useState("");
    const history = useHistory();
    // const [loading, setLoading] = useState(true);
    const [statusCode, setStatusCode] = useState(null);
    useEffect(() => {
        if(localStorage.getItem('user-info') && isLoggedIn == true){
            history.push("/cabinet")
        }
    }, [])

    function validateForm() {
        return Email.length > 0 && Password.length > 0;
    }

  


    async function myLoginAPI1(e) {
        e.preventDefault();
        let item = {Password, Email};
        // try{
        let result = await fetch("https://craftcutstestapiproject20211120225609.azurewebsites.net/api/Customer/Auth", {
            method: 'POST',
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
            },
            body: JSON.stringify({Email: Email, Password: Password}),
        });
        console.log(result);
        console.log(result.status);

        // console.log(result.id);
        if(result.status === 200){

            setStatusCode(200);
            console.log({Email, Password});
            result = await result.json();
            localStorage.setItem("user-info", JSON.stringify(result));
            localStorage.setItem('isLoggedIn', true);
            setIsLoggedIn(true);
            history.push("/cabinet")
            console.log(result);
            console.log(result.status);
            }
            else if(result.status === 404){ 
                setStatusCode(404);
                alert("Вы ввели неправильные данные");
                
            }
            else if(result.status === 400){
                setStatusCode(400);
                alert("Пожалуйста, заполните поля");
            }
        
    }


    return (
        
        <form onSubmit={myLoginAPI1} className={s.formControl}>
        <div className={s.row}>
            <div className={s.col_6}>
                <li className={s.item}>
                    <h5 className={s.logo}>
                        Craft Cuts</h5>  </li>
                <h5 className={s.h5welcome}>Добро пожаловать в <span className={s.craftcuts_red}>Craft Cuts</span> </h5>
                <h6 className = "mb-5 ml-5">У вас нет аккаунта? Зарегестрируйтесь <NavLink to="/registration">здесь</NavLink></h6>
               
                <h1 className={s.login}>Войти</h1>
                <div className={s.Login}>
                <div className = "form-group">
               
                    <input type="email" placeholder="email" onChange={(e)=>setEmail(e.target.value)} className={'form-control' + ' w-50 ' + ' mb-3 ' +  s.email_input}/>
                    <input type="password" placeholder="password" onChange={(e)=>setPassword(e.target.value)} className={'form-control' + ' w-50 ' + s.password_input}/>
                    </div>
                    <button type="submit" className={s.btn_login}>Login </button>
                    {/*<button onClick={myLoginAPI} className={s.btn_login}>Login</button>*/}
                    {/*<NavLink to = "/cabinet" block size="lg" className={s.btn_login}>*/}
                    {/*    Login*/}
                    {/*</NavLink>*/}

                </div>
            </div>
            <div className={s.col_6}>
                <div className={s.img_block}>
                    <img className = {s.imagecol} src={imagecol} alt="image_left"></img></div>
            </div>
        </div>
        </form>
    );
}
