import React, { useEffect, useState } from "react";
import s from "./Review.module.css";
import pic1 from "./pic1.png";
import hotline from "./Shape.png";
import hotlig from "./Path.png";
import axios from 'axios';
import { FaStar } from "react-icons/fa";

let count = 0;
export default function Review() {

    const [Name, setName] = useState("");
    const [Text, setText] = useState("");
    

    async function myReview(e) {        
        e.preventDefault();       
        let obj = JSON.parse(localStorage.getItem('user-info'));
        let id = obj['customer_id'];
        let feedback = document.getElementById('feedback_id').value;
        let item = {id, feedback, count};
        let result = await fetch("https://craftcutstestapiproject20211120225609.azurewebsites.net/api/Review/Create", {
            method: 'POST',
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
            },
            body: JSON.stringify({Customer_Id: id, FeedBack: feedback, Stars: count}),
        });
        


    }

    async function createFeedback(e){
        e.preventDefault();
        let res = await fetch("https://localhost:44312/api/Feedback", {
            method: 'POST',
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
                "Authorization": "Bearer "+ localStorage.getItem("tok"),
            },
            body: JSON.stringify({name: Name, text: Text}),
        });
        console.log(res.status);

    }

    // const [posts, setPosts] = useState([])
    // useEffect(() => {
    //     axios
    //         .get("https://craftcutstestapiproject20211120225609.azurewebsites.net/api/Review/Statistic")
    //         .then(
    //               (response) => {
    //                 console.log(response)
    //                 setPosts(response.data)
    //             })
    // })

    return (
        <div className={s.review}>
            <h1 className={s.h1}>Отзывы про барбершоп</h1>
            <div className={s.row}>
                <div className={s.col6}>
                    <div className={s.blockleft}>
                    <h2 className = "ml-3 pt-3 ">Все отзывы</h2>
                        <div className={s.lefttop}>

                        <div className = {s.scroll}>{posts.map(posts =><div key ={posts.id} className={s.row1}>
                               <div className = {s.namereite}>
                                   <h6 className={s.namerewiew}> Имя: {posts.name}</h6>
                                   <div className={s.stars}>{posts.stars} / 5</div>
                                              </div>             
                                   <h6 className={s.textreview}> Отзыв: {posts.feedback}</h6>
                                </div> )}
                            </div>
                        </div>
                        
                    </div>
                </div>
                <div className={s.col6}>
                    <div className={s.blockright}>
                        <h3 className={s.getreview}>Оставьте свой отзыв здесь!</h3>
                        <h6 className = {s.h6login}>Чтобы оставить свой отзыв вы должны войти в аккаунт</h6>
                        <div>
                            
                            <input type="name" placeholder="name" onChange={(e)=>setName(e.target.value)} className={'form-control' + ' w-50 ' + ' mb-3 ' +  s.email_input}/>
                            <input type="text" placeholder="text" onChange={(e)=>setText(e.target.value)} className={'form-control' + ' w-50 ' + ' mb-3 ' +  s.email_input}/>
                            
                        
                        
                          </div> 
                       <form onSubmit={createFeedback}>  
                       <button type="submit" className = {s.send}>Отправить</button>
                       </form>
                    </div>
                </div>

            </div>
        </div>
    );
}
