import React, {useEffect, useState} from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import s from "./Login.module.css";
import imagecol from "./login_image.png";
import { NavLink } from "react-router-dom";
import Cabinet from "../UserCabinet/Cabinet";
import { useHistory } from 'react-router-dom';



export default function Login({setIsLoggedIn, isLoggedIn, Email, setEmail}) {
  
    const [Password, setPassword] = useState("");

    const [token, setToken] = useState("");
    const [statusCode, setStatusCode] = useState(null);
    const history = useHistory();
    useEffect(() => {
        if(localStorage.getItem('user-info') && isLoggedIn == true){
            history.push("/cabinet")
        }
    }, [])

    async function login(e) {
        e.preventDefault();
        let item = {Password, Email};
        // try{
        let result = await fetch("https://localhost:44350/api/Customer/login", {
            method: 'POST',
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
            },
            body: JSON.stringify({Email: Email, Password: Password}),
        });
        if(result.status === 200){

            setStatusCode(200);
            console.log({Email, Password});
            result = await result.json();
            //console.log(result);
            localStorage.setItem("user-info", JSON.stringify(result));
            localStorage.setItem('isLoggedIn', true);
            localStorage.setItem("tok",result.access_token);
            localStorage['pizda'] = JSON.stringify(result);
            setToken(result);
            console.log(token);

            console.log(localStorage.getItem("tok"));
            setIsLoggedIn(true);
            console.log(result.status);
        }
        else if(result.status === 404){
            setStatusCode(404);
            alert("Вы ввели неправильные данные");

        }
        else if(result.status === 400){
            setStatusCode(400);
            alert("Пожалуйста, заполните поля");
        }

    }
    async function isAdminLogin(){
        let res = await fetch("https://localhost:44312/api/Login/IsAdmin", {
            method: 'GET',
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
                "Authorization": "Bearer "+ localStorage.getItem("tok"),
            },
        });
        console.log(res);
        console.log(res.status);
        res = await res.json();
        localStorage.setItem("isAdminIn", JSON.stringify(res));
    }
    function callTwoFunctionsAtTheSameTime(){
       
        login();
        isAdminLogin();
        console.log(localStorage.getItem("isAdminIn"));
        
    }

    
    return (

        <form onSubmit={callTwoFunctionsAtTheSameTime} className={s.formControl}>
        <div className={s.row}>
            <div className={s.col_6}>
               
                <h5 className={s.h5welcome}>Добро пожаловать в <span className={s.craftcuts_red}>Yotor</span> </h5>
                <h6 className = "mb-5 ml-5">У вас нет аккаунта? Зарегестрируйтесь <NavLink to="/registration">здесь</NavLink></h6>

                <h1 className={s.login}>Войти</h1>
                <div className={s.Login}>
                <div className = "form-group">

                    <input type="email" placeholder="email" onChange={(e)=>setEmail(e.target.value)} className={'form-control' + ' w-50 ' + ' mb-3 ' +  s.email_input}/>
                    <input type="password" placeholder="password" onChange={(e)=>setPassword(e.target.value)} className={'form-control' + ' w-50 ' + s.password_input}/>
                    </div>
                    <button type="submit" className={s.btn_login}>Login </button>
                    

                </div>
            </div>
            <div className={s.col_6}>
                <div className={s.img_block}>
                    <img className = {s.imagecol} src="rentacar-featured.jpg" alt="image_left"></img></div>
            </div>
        </div>
        </form>
    );
}
