import React, {useEffect, useState} from "react";
import s from "./Booking.module.css";
import {NavLink, useHistory} from "react-router-dom";
import Header from "../Header_Footer/Header";
import pic1 from "./master.png";
import pic2 from "./services.png";
import { CalendarComponent } from "@syncfusion/ej2-react-calendars";
import axios from "axios";


export default function Booking() {

    // const [startDate, setStartDate] = useState("");
    // const [endDate, setEndDate] = useState("");
    // const [startAddress, setStartAddress] = useState("");
    // const [endAddress, setEndAddress] = useState("");
    // const [carName, setCarName] = useState("");
    // const [price, setPrice] = useState("100");
    // const[cars, setCars] = useState([]);
    // const[carByParam, setCarByParams] = useState([]);

    const[questName, setQuestName] = useState(0);
    const[questId, setQuestId] = useState(0);
    const[quests, setQuests] = useState([]);
    const [teamName, setTeamName] = useState("");
    const [countOfUsers, setCountOfUsers] = useState(0);
    const [description, setDescription] = useState("");
    const [date, setDate] = useState("");

    

    useEffect(() => {
        axios
            .get("https://localhost:44332/api/Quest",{
                headers:{
                    "Content-Type": "application/json",
                    "Accept": "application/json",
                    "Authorization": "Bearer "+ localStorage.getItem("tok"),
                }
            })
            .then(
                (respon) => {
                    console.log(respon);
                    console.log(respon.data);
                    setQuests(respon.data)})
    },[]);


    async function createBooking(e){
         e.preventDefault();
        
        let res = await fetch("https://localhost:44332/api/Booking/Form", {
            method: 'POST',
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
                "Authorization": "Bearer "+ localStorage.getItem("tok"),
            },
            body: JSON.stringify({TeamName:teamName,CountOfUsers:countOfUsers,Description:description, Date:date}),
        });
        if(res.status==200){
            alert("Успешно");
        }
        else{
            alert("Введите корректные данные");
        }
       console.log(res);
       console.log(res.status);
    }
    
    console.log(quests);

    


    return (
        <div className={s.booking}>
            <div className={s.container}>
                <h1 className={s.h1Online}>Онлайн бронювання</h1>
                <h3 className={s.h3Choose}>Выберите необходимые варианты:</h3>
                <div className={s.container_choose}>


                    <form onSubmit={createBooking} className={s.formControl}>
                        <h4 className={s.h4choose}>Выберите дату</h4>
                        <div className="mb-4">
                            <input onChange = {(e) => {const selectedDate= e.target.value;
                                setDate(selectedDate);
                                console.log(date)}}type="datetime-local" placeholder="date" />
                        </div>  
                        <h4 className={s.h4choose}>Выберите название команды</h4>
                        <div>
                            <form>
                                <input type = "text" placeholder = "Название команды" onChange={(e)=>setTeamName(e.target.value)}  className = {s.input_promo1}></input>
                            </form>
                        </div>
                        <h4 className={s.h4choose}>Выберите количество пользователей</h4>
                        <div>
                            <form>
                                <input type = "text" placeholder = "Количество пользователей" onChange={(e)=>setCountOfUsers(e.target.value)}  className = {s.input_promo1}></input>
                            </form>
                        </div>
                        <h4 className={s.h4choose}>Оставить комментарий</h4>
                        <div>
                            <form>
                                <input type = "text" placeholder = "комментарий" onChange={(e)=>setDescription(e.target.value)}  className = {s.input_promo1}></input>
                            </form>
                        </div>
                        
                        <h4 className={s.h4choose}>Выберите название квеста</h4>
                        <select onChange = {(e) => {const selectedQuest = e.target.value;
                            setQuestName(selectedQuest); for(let i = 0; i <= quests.length; i++){if(questName == quests.name){setQuestId(quests.quest_id)}} }} key ={quests.quest_id}>
                            {quests.map(quests =>
                                <option key = {quests.name}>{quests.name}</option>
                            )}
                        </select>
                        <div> 
                            <button type="submit" className={s.btn_login}>Забронировать </button>
                            

                        </div>
                    </form>
                    
                </div>
            </div>
        </div>
    );
}
