import React from "react";
import Posts from "./Posts";
import s from "./Posts.module.css";
import img1 from "./img1.png";
import img2 from "./img2.png";
import img3 from "./img3.png";
import App from "../../App";
const PostItem = (props) => {

    return (
        <div className={s.col_4} >
            <div>
                <img className={s.img1} src={img1} alt="cut1"></img>
            </div>
            <div className={s.textcard}>
                <h4 className={s.h4_cut}>{props.texthead}</h4>
                <h6 className={s.h6text}>{props.textmain}</h6>
                <div className={s.row}>
                    <h6 className={s.date}>{props.date}</h6>
                    <h5 className={s.read}><a href="#">Read more</a></h5>
                </div>
            </div>
        </div>
    );
}


const AllPosts = (props) => {

 /*   let postsData = [{
        id: 1, texthead: 'Топ стрижек 2021', textmain: 'It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. The point of using Lorem Ipsum is that....'
    }, {
        id: 2, texthead: 'Выбор кресла для барберов', textmain: 'It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. The point of using Lorem Ipsum is that....'
    }, {
        id: 3, texthead: 'Как выбрать “своего” мастера', textmain: 'It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. The point of using Lorem Ipsum is that....'
    }];*/


    let postsElement = props.postsData.map(p => <App texthead = {p.texthead} textmain = {p.textmain} />);


    return (
        <div className={s.blog}>
            <h1 className={s.blogh1}>Блог</h1>
            <div className={s.container}>
                <div className={s.row}>
                   
                    <PostItem img="img1" texthead={postsElement[0]} textmain={postsElement.textmain} date="May 20th 2020" />
                    <PostItem img="img2" texthead={postsElement.texthead} textmain={postsElement.textmain} date="May 20th 2020" />
                    <PostItem img="img3" texthead={postsElement[2].texthead} textmain={postsElement[2].texthead} date="May 20th 2020" />
                </div>
                <div className={s.row}>
                    <PostItem img="img1" texthead="Топ стрижек 2021" textmain="It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. The point of using Lorem Ipsum is that...." date="May 20th 2020" />
                    <PostItem img="img2" texthead="Выбор кресла для барберов" textmain="It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. The point of using Lorem Ipsum is that...." date="May 20th 2020" />
                    <PostItem img="img3" texthead="Как выбрать “своего” мастера" textmain="It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. The point of using Lorem Ipsum is that...." date="May 20th 2020" />
                </div>
                <div className={s.row}>

                    <PostItem img="img1" texthead="Топ стрижек 2021" textmain="It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. The point of using Lorem Ipsum is that...." date="May 20th 2020" />
                    <PostItem img="img2" texthead="Выбор кресла для барберов" textmain="It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. The point of using Lorem Ipsum is that...." date="May 20th 2020" />
                    <PostItem img="img3" texthead="Как выбрать “своего” мастера" textmain="It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. The point of using Lorem Ipsum is that...." date="May 20th 2020" />
                </div>
            </div>
        </div>
    );
}

export default AllPosts;
