import React, {useState, useEffect} from "react";
import slide1 from "../components/images/slide-1.png";
import slide2 from "../components/images/slide-2.jpeg";
import slide3 from "../components/images/slide-3.jpeg";
import slide4 from "../components/images/slide-4.jpg";
import slide5 from "../components/images/slide-5.jpeg";

import "../style/slider.css";

const img = [
  <img key={slide1} src={slide1} alt = "slide1"/>,
  <img key={slide2} src={slide2} alt = "slide2"/>,
  <img key={slide3} src={slide3} alt = "slide3"/>,
  <img key={slide4} src={slide4} alt = "slide4"/>,
  <img key={slide5} src={slide5} alt = "slide5"/>,
]

export function Slider() {
  // Индекс текущего слайда
const [activeIndex, setActiveIndex] = useState(0);

// Хук Effect
useEffect(() => {
    // Запускаем интервал
    const interval = setInterval(() => {
        // Меняем состояние
        setActiveIndex((current) => {
            // Вычисляем индекс следующего слайда, который должен вывестись
            const res = current === img.length - 1 ? 0 : current + 1
            // Возвращаем индекс
            return res
        })
    }, 2000)
    // Выключаем интервал
    return () => clearInterval()
}, [])

// Вычисляем индекс предыдущего слайда
const prevImgIndex = activeIndex ? activeIndex - 1 : img.length - 1
// Вычисляем индекс следующего слайда
const nextImgIndex = activeIndex === img.length - 1 ? 0 : activeIndex + 1

return (
  <div className="slider">
      <div className="slider-img slider-img-prev"
              key={prevImgIndex}>
          {img[prevImgIndex]}
      </div>
      <div className="slider-img"
              key={activeIndex}>
          {img[activeIndex]}
      </div>
      <div className="slider-img slider-img-next"
              key={nextImgIndex}>
          {img[nextImgIndex]}
      </div>
  </div>
)


}

const MainSlider = () => {
  return (
     <main>
    <div className="mainpage">
      <div className="slider">
        <div className="slide1">
          <img className="slide1" src={slide1} alt="slide1"></img>
        </div>
      </div>
    </div>
    </main> 
  );
};
export default Slider;
