import React, {useEffect, useState} from "react";
import s from "./Cabinet.module.css";
import {NavLink, useHistory} from "react-router-dom";
import Header from "../Header_Footer/Header";
import usericon from "../UserCabinet/user_icon.png";
import starpic from "../UserCabinet/heart.png";
import axios from "axios";

const Cabinet = ({setIsLoggedIn, isLoggedIn}) => {


    const [user, setUser]=useState([]);
    const history = useHistory();
    function logout(){
        setIsLoggedIn(false);
        // if(isLoggedIn == false){
        //     history.push("/login")
        // }
    }

    const handleLogOut = () => {
        setIsLoggedIn(false);
    };

    useEffect(() => {

      axios
          .get("https://localhost:44312/api/Login/Info",{
              headers:{
                  "Content-Type": "application/json",
                  "Accept": "application/json",
                  "Authorization": "Bearer "+ localStorage.getItem("tok"),
              }
          })
          .then(
              (respon) => {
                  console.log(respon);
                  console.log(respon.data);
                  setUser(respon.data)})
  },[]);
  console.log(user);

  <Header />;
  return (
    <div className={s.cabinet_wrapper}>
      <div className={s.wrapper}>
        <h2 className={s.h2text}>Личный кабинет</h2>
        <div className={s.infouser_block}>
          <div className={s.row}>
            <h3 className={s.username}>John</h3>
            <div className={s.btn_change}>
              <NavLink onClick={handleLogOut}
                       className={`me-3 ${s.change}`}
                      to="/login"
                      activeClassName={s.active}>
              Выйти
              </NavLink>
            </div>
          </div>

        
          <div className = {s.row}>
        <div className = {s.receptions_block}>
            <div className = {s.row}>
                <h5 className = {s.h5_resept}>Ваши записи</h5>
                
            </div>
            <div className = {s.near}><span>Ближайшие</span></div>
            <div className = "coming"><span></span></div>
            <div className={s.row}>
                <img className = {s.usericon_pic} src = {usericon} alt = "userpic"></img>
                <span className = {s.textspan}>Барбер Дима - </span>
                <span className = {s.textspan}>Стрижка - </span>
                <span className = {s.textspan}>12.06 16:00</span>
                <button className = {s.starpic}></button>
            </div>
            <div className = {s.near}><span>Предстоящие</span></div>
            <div className={`${s.row} ${s.row1}`}>
                <img className = {s.usericon_pic} src = {usericon} alt = "userpic"></img>
                <span className = {s.textspan}>Барбер Дима - </span>
                <span className = {s.textspan}>Стрижка - </span>
                <span className = {s.textspan}>15.08 16:00</span>
                <button className = {s.starpic}></button>
            </div>
            <NavLink
                  className={` ${s.btn_online}`}
                  to="/online"
                  activeClassName={s.active}
                >
                  Записаться онлайн
            </NavLink>
        </div>
            {/*<button className = {s.promo_btn} onClick={logout}>Выйти</button>*/}
            <div className = {s.receptions_block}>
            <div className = {s.row}>
                <h5 className = {s.h5_resept}>Доступные промокоды</h5>
                
            </div>
           <div className = {s.promocodes}>
            <input type = "text" value = "VF0ZC7HSNX"  className = {s.input_promo1}></input>
            <input type = "text" value = "OAR9QE5JDI"  className = {s.input_promo1}></input>
            <input type = "text" value = "FDMNEIMVI3"  className = {s.input_promo1}></input>
            <input type = "text" value = "KCVHWBSN4F"  className = {s.input_promo1}></input>
            <input type = "text" value = "LHNCGDBRNS"  className = {s.input_promo1}></input>
            </div>
           
            </div>
           
        </div>
           </div>
        </div>
      </div>
  
  );
};

export default Cabinet;
