import React, { useEffect, useState } from "react";
import s from "./Review.module.css";



export default function Review() {

    const [Name, setName] = useState("");
    const [Text, setText] = useState("");
    
    
    async function getFeedbacks(e){
        //e.preventDefault();
        let res = await fetch("https://localhost:44312/api/Feedback", {
            method: 'GET',
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
                "Authorization": "Bearer "+ localStorage.getItem("tok"),
            },
        }); 
        res = await res.json();
        localStorage.setItem("feedbacks", JSON.stringify(res));
        console.log(localStorage.getItem("feedbacks"));
    };

    
    function callTwoFunctions(){
        createFeedback();
        getFeedbacks();
    }
    
     

    async function createFeedback(e){
        //e.preventDefault();
        let res = await fetch("https://localhost:44312/api/Feedback", {
            method: 'POST',
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
                "Authorization": "Bearer "+ localStorage.getItem("tok"),
            },
            body: JSON.stringify({Name: Name, Text: Text}),
        });
        console.log(res.status);

    };
    getFeedbacks();
    return (
        <div className={s.review}>
            <h1 className={s.h1}>Отзывы пользователей</h1>
            <div className={s.row}>
                <div className={s.col6}>
                    <div className={s.blockleft}>
                    <h2 className = "ml-3 pt-3 ">Все отзывы</h2>
                         <div className={s.lefttop}> 
                         { JSON.parse(localStorage.getItem("feedbacks")).map(item => { 
                    return (

                        <tr>  
                            <td> {item.name} </td>
                            <td>   </td>
                            <td> {item.text}</td>
                            
                        </tr>
                    );
                })}

                        
                        </div> 
                        
                    </div>
                </div>
                <div className={s.col6}>
                    <div className={s.blockright}>
                        <h3 className={s.getreview}>Оставьте свой отзыв здесь!</h3>
                        <h6 className = {s.h6login}>Чтобы оставить свой отзыв вы должны войти в аккаунт</h6>
                        <div>
                            
                            <input type="name" placeholder="name" onChange={(e)=>setName(e.target.value)} className={'form-control' + ' w-50 ' + ' mb-3 ' +  s.email_input}/>
                            <input type="text" placeholder="text" onChange={(e)=>setText(e.target.value)} className={'form-control' + ' w-50 ' + ' mb-3 ' +  s.email_input}/>
                            
                        
                        
                          </div> 
                       <form onSubmit={callTwoFunctions}>  
                       <button type="submit" className = {s.send}>Отправить</button>
                       </form>
                    </div>
                </div>

            </div>
        </div>
    );
}
