import React, {useState} from "react";
import s from "./Booking.module.css";
import {NavLink, useHistory} from "react-router-dom";
import Header from "../Header_Footer/Header";
import pic1 from "./master.png";
import pic2 from "./services.png";
import { CalendarComponent } from "@syncfusion/ej2-react-calendars";


export default function Booking() {
   /* const dateValue: Date = new Date(new Date().getFullYear(), new Date().getMonth, 20)
;  */
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [BarberName, setBarberName] = useState("");
    const [phone, setPhone] = useState("");
    const [birthday, setBirthday] = useState("");
    const history = useHistory();
    async function BookingApi(e) {
        e.preventDefault();
        // try{
        let result = await fetch("https://craftcutstestapiproject20211120225609.azurewebsites.net/api/Customer/Auth", {
            method: 'POST',
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
            },
            body: JSON.stringify({Email: Email, Password: Password}),
        });
        console.log(result);
        console.log(result.status);

        // console.log(result.id);
        if(result.status === 200){

            setStatusCode(200);
            console.log({Email, Password});
            result = await result.json();
            localStorage.setItem("user-info", JSON.stringify(result));
            localStorage.setItem('isLoggedIn', true);
            setIsLoggedIn(true);
            history.push("/cabinet")
            console.log(result);
            console.log(result.status);
        }
        else if(result.status === 404){
            setStatusCode(404);
            alert("Вы ввели неправильные данные");

        }
        else if(result.status === 400){
            setStatusCode(400);
            alert("Пожалуйста, заполните поля");
        }

    }




    return (
        <div className={s.booking}>
            <div className={s.container}>
                <h1 className={s.h1Online}>Онлайн запись</h1>
                <h3 className={s.h3Choose}>Выберите необходимые варианты:</h3>
                <div className={s.container_choose}>

                    

                    <form method="post">
                    <h4 className={s.h4choose}>Выберите мастера</h4>
                        <select required>
                            <option>Женя</option>
                            <option>Дима</option>
                            <option>Егор</option>
                            <option>Иван</option>
                            <option>Саша</option>
                        </select>

                        <h4 className={s.h4choose}>Выберите услугу</h4>
                        <select required>
                            <option>Стрижка</option>
                            <option>Покраска</option>
                            <option>Кастом</option>
                            <option>Бритье</option>
                            <option>Стрижка бороды</option>
                        </select>


                        <h4 className={s.h4choose}>Выберите время</h4>
                        <input type="datetime-local" placeholder="time" onChange={(e)=>setBirthday(e.target.value)} className="w-75" />
                            {/*<CalendarComponent ></CalendarComponent>*/}
                            <NavLink to = "/endbooking" type = "submit" className = {s.btnbook} >Записаться</NavLink>
                        
                    </form>

                    <div className={s.service}></div>
                    <div className={s.time}></div>
                </div>
            </div>
        </div>
    );
}
