import React, {useEffect, useState} from "react";
import {Card,CardBody,CardHeader, Col, Row, Table} from 'reactstrap';
import {NavLink, useHistory} from "react-router-dom";





function UserList(){
    const history = useHistory();

    async function deleteUser(id){
        let res = await fetch("https://localhost:44379/api/User/" + id, {
            method: 'DELETE',
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
                "Authorization": "Bearer "+ localStorage.getItem("tok"),
            },
        });
    }

    const editUser = (id)=>{
        localStorage.setItem("UserId",id);

        history.push({
            pathname : './EditUser'
        })

    }



    function callTwoFunctions(id){
        deleteUser(id);
        getUsers();
    }


    async function getUsers(){

        let res = await fetch("https://localhost:44332/api/User", {
            method: 'GET',
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
                "Authorization": "Bearer "+ localStorage.getItem("tok"),
            },
        });
        res = await res.json();
        localStorage.setItem("use", JSON.stringify(res));

    }
    //debugger;
    getUsers();


    return (
        <div className="animated fadeIn">
            <Row>
                <Col>
                    <Card>
                        <CardHeader>
                            <i className="fa fa-align-justify"></i> Users List
                        </CardHeader>
                        <CardBody>
                            <Table  striped size ="sm">
                                <thead>
                                <tr>
                                    <th>Id</th>
                                    <th>Name</th>
                                    <th>Description</th>
                                    <th>Difficulty_level</th>
                                    <th>City</th>
                                    <th>Address</th>
                                    <th>Category</th>
                                    <th>Actors</th>
                                    <th>Company id</th>
                                    <th>Max count of users</th>
                                    <th>Price</th>
                                </tr>
                                </thead>
                                <tbody>
                                {
                                    JSON.parse(localStorage.getItem("use")).map((item,idx)=>{
                                        return <tr key={idx}>
                                            <td>{item.user_id}</td>
                                            <td>{item.email}</td>
                                            <td>{item.phone}</td>
                                            <td>{item.password}</td>
                                            <td>{item.name}</td>
                                            <td>{item.role}</td>
                                            <td>{item.company_id}</td>
                                            <td>
                                                <row>
                                                    <button className="btn btn-warning" onClick={()=>editUser(item.user_id)}>Edit</button>
                                                </row>
                                                <row>
                                                    -
                                                </row>
                                                <row>
                                                    <button className="btn btn-warning" onClick={()=>callTwoFunctions(item.user_id)}>Delete</button>
                                                </row>
                                            </td>

                                        </tr>
                                    })
                                }
                                </tbody>
                            </Table>
                        </CardBody>
                    </Card>
                </Col>
            </Row>
        </div>
    )
}
export default UserList;
