import React, {useEffect, useState} from "react";
import s from "./Booking.module.css";
import {NavLink, useHistory} from "react-router-dom";
import Header from "../Header_Footer/Header";
import pic1 from "./master.png";
import pic2 from "./services.png";
import { CalendarComponent } from "@syncfusion/ej2-react-calendars";
import axios from "axios";


export default function Booking() {

    const [startDate, setStartDate] = useState("");
    const [endDate, setEndDate] = useState("");
    const [startAddress, setStartAddress] = useState("");
    const [endAddress, setEndAddress] = useState("");
    const [carName, setCarName] = useState("");
    const [price, setPrice] = useState(100);
    const[cars, setCars] = useState([]);
    const[carByParam, setCarByParams] = useState([]);
    

    useEffect(() => {

        axios
            .get("https://localhost:44312/api/Car",{
                headers:{
                    "Content-Type": "application/json",
                    "Accept": "application/json",
                    "Authorization": "Bearer "+ localStorage.getItem("tok"),
                }
            })
            .then(
                (respon) => {
                    console.log(respon);
                    console.log(respon.data);
                    setCars(respon.data)})
    },[]);


    async function createBooking(){
        
        let res = await fetch("https://localhost:44312/api/Booking", {
            method: 'POST',
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
                "Authorization": "Bearer "+ localStorage.getItem("tok"),
            },
            body: JSON.stringify({Start_date:startDate,End_date:endDate,Start_address:startAddress, End_address:endAddress, Car_name:carName, Full_price:price}),
        });
        console.log(res);
        console.log(res.status);
        
    }
    async function getBookingByParams(){
       
        let res = await fetch("https://localhost:44312/api/Booking/Params", {
            method: 'POST',
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
                "Authorization": "Bearer "+ localStorage.getItem("tok"),
            },
            body: JSON.stringify({Start_date:startDate,End_date:endDate,Start_address:startAddress, End_address:endAddress}),
        });
        res = await res.json();
        
        console.log(res);
        console.log(res.status);
        localStorage.setItem("carByParam", JSON.stringify(res));
        console.log(localStorage.getItem("carByParam"));

    }
    function callFunctions(){
        createBooking();
        getBookingByParams();

    }
    console.log(cars);

    //------------------------------------------------

    const [barberName, setBarberName] = useState("");
    const [servicePrice, setServicePrice] = useState("");
    const [serviceName, setServiceName] = useState("");
    const [date, setDateName] = useState("");
    const history = useHistory();
    const [barber, setBarber] = useState([]);
    const [promocode, setPromocode] = useState([]);


    let serviceName1 = localStorage.getItem('bookingServiceName-info');
    let servicePrice1 = localStorage.getItem('bookingServicePrice-info');
    let barber1 = localStorage.getItem('bookingBarber-info');
    let date1 = localStorage.getItem('bookingDate-info');
    let obj1 = JSON.parse(localStorage.getItem('user-info'));
    let email1 = obj1['email'];
    let b = JSON.parse(localStorage.getItem('email'));
    const [Email, setEmail] = useState(b);

    console.log(typeof(serviceName));

    useEffect(() => {
        axios
            .get("https://craftcutstestapiproject20211120225609.azurewebsites.net/api/Barber")
            .then(
                (respon) => {
                    console.log(respon)
                    setBarber(respon.data)})
    },[]);

    const [service, setService] = useState([])
    useEffect(() => {
        axios
            .get("https://craftcutstestapiproject20211120225609.azurewebsites.net/api/Service")
            .then(
                (respon) => {
                    console.log(respon)
                    setService(respon.data)})
    },[]);




    localStorage.setItem("bookingServiceName-info", serviceName);
    localStorage.setItem("bookingServicePrice-info", servicePrice);
    localStorage.setItem("bookingBarber-info", barberName);
    localStorage.setItem("bookingDate-info", date);



    async function BookingApi(e) {
        e.preventDefault();

        let result = await fetch("https://craftcutstestapiproject20211120225609.azurewebsites.net/api/Booking/Web", {
            method: 'POST',
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
            },
            body: JSON.stringify({ BarberName: barberName, CustomerEmail: email1, ServiceName: serviceName, Date: date, PromocodeName: promocode}),
        });

        console.log(result);
        console.log(result.status);

        // console.log({barberName, customerEmail, hookServiceName, hookDate});
        console.log({barber1, email1, serviceName, date, promocode});
        result = await result.json();
        localStorage.setItem("booking-info", JSON.stringify(result));
        // localStorage.setItem('isLoggedIn', true);
        // setIsLoggedIn(true);
        history.push("/endbooking")
        console.log(result);
        console.log(result.status);

    }
console.log(cars);
console.log(service);

    return (
        <div className={s.booking}>
            <div className={s.container}>
                <h1 className={s.h1Online}>Онлайн бронювання</h1>
                <h3 className={s.h3Choose}>Выберите необходимые варианты:</h3>
                <div className={s.container_choose}>

                    <form method="post">
                        <h4 className={s.h4choose}>Выберите начальную дату</h4>
                        <div className="mb-4">
                            <input onChange = {(e) => {const selectedStartDate= e.target.value;
                                setStartDate(selectedStartDate);}}type="datetime-local" placeholder="password" />
                        </div>

                        <h4 className={s.h4choose}>Выберите конечную дату</h4>
                        <div className="mb-4">
                            <input onChange = {(e) => {const selectedEndDate= e.target.value;
                                setEndDate(selectedEndDate);}}type="datetime-local" placeholder="password" />
                        </div>  
                        <h4 className={s.h4choose}>Выберите начальный адрес</h4>
                        <div>
                            <form>
                                <input type = "text" placeholder = "Начальный адрес" onChange={(e)=>setStartAddress(e.target.value)}  className = {s.input_promo1}></input>
                            </form>
                        </div>
                        <h4 className={s.h4choose}>Выберите конечный адрес</h4>
                        <div>
                            <form>
                                <input type = "text" placeholder = "Конечный адрес" onChange={(e)=>setEndAddress(e.target.value)}  className = {s.input_promo1}></input>
                            </form>
                        </div>
                        
                        <h4 className={s.h4choose}>Выберите название машины</h4>
                        <select onChange = {(e) => {const selectedCar = e.target.value;
                            setCarName(selectedCar);}} key ={cars.car_id}>
                            {cars.map(cars =>
                                <option key = {cars.model}>{cars.model}</option>
                            )}
                        </select>                
                                        


                        <NavLink to={{ pathname: '/EndBooking'  }} onClick={callFunctions} type = "button" className = {s.btnbook}>Забронировать</NavLink>
                    </form>
                    
                </div>
            </div>
        </div>
    );
}
